import React ,{useEffect , useState} from "react"
import "./transbtn.css"

export default function TransBtn ( {setOutput} ) {
    const [input, setInput] = useState("");
    const [sourceLang, setSourceLang] = useState("");
    const [targetLang, setTargetLang] = useState("");

    const fetchData = async ({ setOutput }) => {
        const params = new URLSearchParams({
            text: input,
            source_lang: sourceLang,
            target_lang: targetLang,
        });

        const url = `http://localhost:5000/api/tencent_translate?${params.toString()}`;

        try {
            const response = await fetch(url);

            const reader = response.body.getReader()
            let result = '';
            while (true) {
              const { done, value } = await reader.read();
              if (done) break;
              result += new TextDecoder().decode(value);
            }
            console.log(result);
            const jsonResponse = await result.json();  // 解析 JSON 响应
            const neededData = jsonResponse[0];  // 获取数组中的第一个元素
            setOutput(neededData);  // 使用 setOutput 更新数据

          } catch (error) {
            console.error('Fetch error:', error);
          }
    };

    const translate = () => {
    setInput(document.getElementById("input").value)
    setSourceLang(document.getElementById("sourceLang").value)
    setTargetLang(document.getElementById("targetLang").value)

    fetchData({ setOutput })
    }

    return (
        <div>
            <div className='button_container'>
            <span className='lang_button_container'>
                <span>源语言</span>
                <select className='button' id="sourceLang">
                    <option value="en">英文</option>
                </select>
            </span>
            <button className='button' id="trans" onClick={translate}>翻译</button>
            <span className='lang_button_container'>
                <span>目标语言</span>
                <select className='button' id="targetLang">
                    <option value="zh">中文</option>
                </select>
            </span>
            </div>
        </div>
    )
}
