import React ,{useEffect , useState} from "react"
import "./transbtn.css"

export default function TransBtn () {
    const [input, setInput] = useState("");
    const [sourceLang, setSourceLang] = useState("");
    const [targetLang, setTargetLang] = useState("");

    const fetchData = async () => {
        const params = new URLSearchParams({
            text: input,
            source_lang: source_lang,
            target_lang: target_lang
        });
    };

    const translate = () => {
    setInput(document.getElementById("input").value)
    setSourceLang(document.getElementById("sourceLang").value)
    setTargetLang(document.getElementById("targetLang").value)

    fetchData()
    }

    return (
        <div>
            <div className='button_container'>
            <span className='lang_button_container'>
                <span>源语言</span>
                <select className='button' id="source_lang">
                {/* 在此处添加语言选项 */}
                </select>
            </span>
            <button className='button' id="trans" onClick={translate}>翻译</button>
            <span className='lang_button_container'>
                <span>目标语言</span>
                <select className='button' id="target_lang">
                {/* 在此处添加语言选项 */}
                </select>
            </span>
            </div>
        </div>
    )
}
