import React ,{useEffect , useState} from "react"
import "./App.css"

import IdKeySave from "./components/id_key_save";

function App() {
  const [input, setInput] = useState("");
  const [sourceLang, setSourceLang] = useState("");
  const [targetLang, setTargetLang] = useState("");
  const translate = () => {
    input = setInput(document.getElementById("input").value)
    sourceLang = setSourceLang(document.getElementById("sourceLang").value)
    targetLang = setTargetLang(document.getElementById("targetLang").value)
  }
  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("http://localhost:5000/api/tencent_translate");
      const reader = response.body.getReader();
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;
        // 处理获取到的数据片段
        console.log(new TextDecoder().decode(value));
      }
    };
  
    fetchData();
  }, []);

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>长文本翻译器--Powered by Tencent</h1>
      <IdKeySave />
      <div className='container'>
        <p>源文本</p>
        <textarea className='text_container' placeholder="在这里输入文字或拖入文件" id="input"/>
        <div className='button_container'>
          <span className='lang_button_container'>
            <span>源语言</span>
            <select className='button'>
              {/* 在此处添加语言选项 */}
            </select>
          </span>
          <button className='button' id="trans" onClick={translate}>翻译</button>
          <span className='lang_button_container'>
            <span>目标语言</span>
            <select className='button'>
              {/* 在此处添加语言选项 */}
            </select>
          </span>
        </div>
        <div style={{display:'none'}}>翻译中...</div>
        <textarea className='text_container' placeholder="这里是等会翻译后的文本" readOnly id="output"/>
      </div>
    </div>
  );
}  

export default App;
