import React , {useState , useEffect} from "react";

export default function Output ({ onUpdata }) {
    const [output, setOutput] = useState("");
    // 用于更新输出的函数
    const updateOutput = (newOutput) => {
        setOutput(newOutput);
    };

    // 当组件加载时，将更新函数传递给父组件
    useEffect(() => {
        onUpdate(updateOutput);
    }, [onUpdate]);
    return (
        <div className="container">
            <div style={{display:'none'}}>翻译中...</div>
            <textarea className='text_container' placeholder="这里是等会翻译后的文本" readOnly id="output"/>
        </div>
    )
}
